import React from 'react';

const Hero = () => {
  return (
    <div
      className="container-fluid bg-cover bg-center"
      style={{ backgroundImage: 'url("/images/mainbg.png")', height: '100vh' }}
    >
      <div className="flex h-1/2 items-center ms-lg-5">
        <div className="lg:w-1/3 mt-5 text-lg-start text-center lg:ml-5">
          <h1 className=" font-bold text-white">
            Hey I'm <br></br> <span id='bigtext' className="animate-charcter">BUBUN</span>
          </h1>
          <p className="text-white">A Passionate Web developer.....</p>
        </div>
      </div>

      <div className="text-center mt-5">
        <button
          onClick={() => window.location.href = '#contact'}
          className="bttn font-bold px-5 py-3 text-lg rounded-lg border-0"
          type="button"
        >
          Contact Me
        </button>
      </div>
    </div>
  );
}

export default Hero;
